    jQuery(function(e){e('iframe[src*="player.vimeo.com"]').wrap('<div class="js--video" />').parent(),e('iframe[src*="youtube.com"]').wrap('<div class="js--video" />').parent(),e(".js--video").fitVids(),e("iframe").attr("frameborder","0")});

